mapper.select:
SELECT * FROM gap_reviews


        AND major = #{major}


        AND ismajor = #{isMajor}


        AND gap_period_months <= #{maxGapMonths}


ORDER BY created_at DESC

SELECT gap_reviews_id
FROM gap_review_bookmarks
WHERE user_id = #{userId}

SELECT * FROM gap_reviews
WHERE user_id = #{userId}
ORDER BY created_at DESC
